import React, { Component } from "react";
import Showcase from "../components/Showcase";
import TopMovies from "../components/TopMovies";
import { store } from "../store";

class HomeContainer extends Component {
  state = {
    videos: [],
    switchOff: false,
    switchOn: true,
  };

  componentDidMount() {
    fetch("http://localhost:3000/movies")
      .then((resp) => resp.json())
      .then((videos) => {
        this.setState({ videos });
      });
  }

  addToWatchlist = (mov) => {
    store.watchlist.push(mov);
  };

  changeButton = (previousState) => {
    this.setState({
      switchOff: !previousState.switchOff,
    });
  };

  videoShowcase = () => {
    return this.state.videos.map((mov) => (
      <div className="movie-card">
        <Showcase mov={mov} />
        <div className="like-links">
          <button
            onClick={() => this.addToWatchlist(mov)}
            onChange={this.changeButton}
          >
            {this.state.switchOff
              ? "Add to Watchlist"
              : "Remove from Watchlist"}
          </button>
          <button>Best Live-Action</button>
          <button>Best Animated</button>
        </div>
      </div>
    ));
  };

  render() {
    return (
      <div className="home">
        <TopMovies />
        {this.videoShowcase()}
      </div>
    );
  }
}

export default HomeContainer;
