import React, { Component } from "react";
import Showcase from "../components/Showcase";
import { connect } from "react-redux";
import Intro from "../components/Intro";

class HomeContainer extends Component {
  state = {
    showStory: false,
  };

  componentDidMount() {
    fetch("http://localhost:3000/movies")
      .then((resp) => resp.json())
      .then((videos) => {
        const newVideos = videos.map((video) => ({
          ...video,
          onList: false,
        }));
        this.props.setVideos(newVideos);
      });
  }

  // addToWatchlist = (mov) => {
  //   store.watchlist.push(mov);
  // };

  videoShowcase = () => {
    const { videos } = this.props;
    return videos.map((mov) => (
      <div className="movie-card">
        <Showcase mov={mov} />
      </div>
    ));
  };

  handleClick = () => {
    this.setState({ showStory: !this.state.showStory });
  };
  // topMovies = () => {
  //   const { videos } = this.props;
  //   const slicedVideo = videos.splice(-2, 2);
  //   return slicedVideo.map((mov) => (
  //     <div className="movie-card">
  //       <TopMovies videos={mov} />
  //       <div className="like-links">
  //         <button onClick={() => this.addToWatchlist(mov)}>
  //           Add to Watchlist
  //         </button>
  //       </div>
  //     </div>
  //   ));
  // };

  render() {
    return (
      <div className="show-title">
        <h1 onClick={() => this.handleClick()}>Animator's Showcase</h1>
        <div className="intro">
          <a className="hidden" href="#">
            <p>The story so far ...</p>
          </a>
          <Intro />
        </div>
        <div className="home">{this.videoShowcase()}</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  videos: state.videos,
  watchList: state.watchList,
});

const mapDispatchToProps = (dispatch) => ({
  setVideos: (videos) => {
    dispatch({
      type: "SET_VIDEOS",
      payload: {
        videos: videos,
      },
    });
  },
});

export default connect(mapStateToProps, mapDispatchToProps)(HomeContainer);
