export const uploadVideo = (uploads) => {
  return {
    type: "UPLOAD_VIDEO",
    uploads: Object.assign({}, uploads),
  };
};

export const postQuestion = (text) => {
  return (dispatch) => {
    fetch("http://localhost:3000/questions", {
      method: "POST",
      headers: { "Content-type": "application/json" },
      body: JSON.stringify({ title: text }),
    })
      .then((resp) => resp.json())
      .then((question) => {
        dispatch({ type: "ADD_QUESTION", text });
      });
  };
};

export const getVideos = () => {
  return (dispatch) => {
    fetch("http://localhost:3000/movies")
      .then((resp) => resp.json())
      .then((videos) => {
        dispatch({
          type: "SET_VIDEOS",
          payload: {
            videos: videos,
          },
        });
      });
  };
};
