const Intro = () => {
  function showHidden() {
    document.getElementById("hidden").style.display = "block";
  }
  return (
    <div>
      <h2 onClick={(document.getElementById("hidden").style.display = "block")}>
        The story so far...
      </h2>
      <div id="hidden" style="display: none">
        <p>This is where I describe the film festival.....</p>
      </div>
    </div>
  );
};

export default Intro;
